const mysql = require('mysql');
const express = require('express');

const con = mysql.createConnection({
  host: "localhost",
  user: "root",
  password: "1234",
  database: 'testc'
});
con.connect(function(err) {
  if (err) throw err;
  app.get('/menu',function(req,res){
  con.query("SELECT * FROM ex_details", function (err, result, fields) {
    if (err) throw err;
    console.log(result);
  });
});
});
