import React from 'react';
import {BrowserRouter as Router,Route,Link,Switch, Redirect} from "react-router-dom";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Details from './components/Details.js'
import Userlist from './pro/Userlist';
import Dashboard from './pro/Dashboard';
import Indexx from './pro/Indexx';
import { CookiesProvider,useCookies,Cookies } from 'react-cookie';
import AuthApi from './AuthApi';
const cookies = new Cookies();
const GCC=cookies.get('name');

function App() {
    const [auth,setAuth]=React.useState(false);
  return (
<div>       
        <Switch>
            <Route exact path="/" component={Dashboard} />
            <Route path="/start"  component={Indexx}  />
            <Route path="/userlist" component={Userlist} />
        </Switch>
        <AuthApi.Provider value={(auth,setAuth)}>
           <Router>
               <Routes />
           </Router>
        </AuthApi.Provider>
</div>
  );
}

const Login=()=>{
    const Auth=React.useContext(AuthApi)
    const clickl = () =>{
       Auth.setAuth(true);
       Cookies.set("user","login");
    }
    return(
        <div>
            <h3>login page</h3>
            <button onClick={clickl}>login</button>
        </div>
    )
}
const DDashboard=()=>{
    const Auth=React.useContext(AuthApi)
    const clicklt = () =>{
      //Auth.setAuth(false);
      //Cookies.remove('user','login');
    }
    return(
        <div>
            <h3>Dashboard</h3>
            <button onClick={clicklt}>logout</button>
        </div>
    )
}
const ProtectedRoute=({auth,component:Component,...rest})=>{
    return(
        <Route 
           {...rest}
           render={()=>GCC?(
                <Component />
            ):
            (
                <Redirect to="/login" />
            )
         }        
        />  
    )
}
const ProtectedLogin=({auth,component:Component,...rest})=>{
    const Auth=React.useContext(AuthApi)
    return(
        <Route 
            {...rest}
            render={()=>!auth?(
                <Component />
            ):
            (
                <Redirect to="/DDashboard" />
            )
         }       
        />  
    )
}
const Routes=()=>{
    return(
        <Switch>
            <Route exact path="/login" component={Login} auth={Auth.auth}/>
            <Route path="/DDashboard"  auth={Auth.auth} component={DDashboard}  />
        </Switch>
    )
}
export default App;
