import React from 'react';
import {BrowserRouter as Router,Route,Link,Switch, Redirect} from "react-router-dom";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Details from './components/Details.js'
import Menu from './pro/Menu';
import Userlist from './pro/Userlist';
import Dashboard from './pro/Dashboard';
import Indexx from './pro/Indexx';
import { CookiesProvider,useCookies } from 'react-cookie';
const AuthApi=React.createContext();
function App() {
    const [auth,setAuth]=React.useState(false);
  return (
<div>
        <Switch>
                <Route exact path="/" component={Indexx} />
                <Route path="/menu" component={Menu} />
                <Route path="/dashboard" component={Dashboard} />
                <Route path="/userlist" component={Userlist} />
                <Route path="/details" component={Details} />
        </Switch>
        <AuthApi.Provider value={auth,setAuth}>
            <Router>
                <Routes />
            </Router>
        </AuthApi.Provider>
</div>
  );
}
const Login=()=>{
    const Auth=React.useContext(AuthApi)
    const clickl = () =>{
      Auth.setAuth(true);
      Cookies.set("user","login");
    }
    return(
        <div>
            <h3>login page</h3>
            <button onClick={clickl}>login</button>
        </div>
    )
}
const DDashboard=()=>{
    const Auth=React.useContext(AuthApi)
    const clicklt = () =>{
      Auth.setAuth(false);
      //Cookies.remove('user','login');
    }
    return(
        <div>
            <h3>Dashboard</h3>
            <button onClick={clicklt}>logout</button>
        </div>
    )
}
const Routes=()=>{
    const Auth=React.useContext(AuthApi);
    return(
        <switch>
            <Prrl path="/login" component={Login} auth={Auth.auth}/>
            <Prr path="/ddashboard" auth={Auth.auth}component={DDashboard}/>
        </switch>
    )
}
const Prr=({auth,component:Component,...rest})=>{
    return(
        <Route 
           {...rest}
           render={()=>auth?(
               <Component />
           ):
           (
               <Redirect to="/login" />
           )
        }

        />  
    )
}
const Prrl=({auth,component:Component,...rest})=>{
    return(
        <Route 
           {...rest}
           render={()=>!auth?(
               <Component />
           ):
           (
               <Redirect to="/DDashboard" />
           )
        }

        />  
    )
}
export default App;
