import React from 'react';
import {BrowserRouter as Router,Route,Link,Switch, Redirect} from "react-router-dom";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Details from './components/Details.js'
import Userlist from './pro/Userlist';
import Dashboard from './pro/Dashboard';
import Indexx from './pro/Indexx';
import { CookiesProvider,useCookies,Cookies } from 'react-cookie';
import AuthApi from './AuthApi';
const cookies = new Cookies();
//const GCC=cookies.get('name');

function App() {
    const [auth,setAuth]=React.useState(false);
    const readCookie=()=>{
        const user=cookies.get('user');
        if(user){
            setAuth(true);
        }
    }
    React.useEffect(()=>{
       readCookie();
    },[]);
  return (
<div>       
        <Switch>
            <Route exact path="/" component={Dashboard} />
            <Route path="/start"  component={Indexx}  />
            <Route path="/userlist" component={Userlist} />
        </Switch>
        <AuthApi.Provider value={(auth,setAuth)}>
           <Router>
               <Routes />
           </Router>
        </AuthApi.Provider>
</div>
  );
}

const Login = () =>{
    const Auth=React.useContext(AuthApi)
    const handleonClick = () =>{
       //Auth.setAuth(true);
      const SCC= cookies.remove("user","loginn");
    }
    return(
        <div>
            <h3>login page</h3>
            <button onClick={handleonClick}>login</button>
        </div>
    )
}
const DDashboard=()=>{
    const Auth=React.useContext(AuthApi)
    const handleonClick = () =>{
      //Auth.setAuth(false);
      cookies.remove('user');
    }
    return(
        <div>
            <h3>Dashboard</h3>
            <button onClick={handleonClick}>logout</button>
        </div>
    )
}
const ProtectedRoute=({SCC,component:Component,...rest})=>{
    return(
        <Route 
           {...rest}
           render={()=>SCC?(
                <Component />
            ):
            (
                <Redirect to="/login" />
            )
         }        
        />  
    )
}
const ProtectedLogin=({SCC,component:Component,...rest})=>{
    const Auth=React.useContext(AuthApi)
    return(
        <Route 
            {...rest}
            render={()=>!SCC?(
                <Component />
            ):
            (
                <Redirect to="/DDashboard" />
            )
         }       
        />  
    )
}
const Routes=()=>{
    const Auth=React.useContext(AuthApi)
    return(
        <Switch>
            <ProtectedLogin exact path="/login" component={Login} auth={Auth.auth}/>
            <ProtectedRoute path="/DDashboard"  auth={Auth.auth} component={DDashboard}  />
        </Switch>
    )
}
export default App;
